function parse_css($css) {\n    $css = preg_replace('/\s+/', ' ', $css); // normalize whitespace\n    $css = preg_replace('/\/\*.*?\*\//', '', $css); // remove comments\n    $rules = preg_split('/}\s*/', $css);\n    $result = array();\n    foreach ($rules as $rule) {\n        if (trim($rule)) {\n            preg_match('/([^{]+)\{(.+)\}/', $rule, $match);\n            if ($match) {\n                $selector = trim($match[1]);\n                $styles = explode(';', $match[2]);\n                $result[$selector] = array();\n                foreach ($styles as $style) {\n                    list($property, $value) = explode(':', $style);\n                    $result[$selector][] = array(trim($property) => trim($value));\n                }\n            }\n        }\n    }\n    return $result;\n}\n\n$css = file_get_contents('path/to/your/css/file.css');\n$result = parse_css($css);\nprint_r($result);