$fields = array('field1' => 'value1', 'field2' => 'value2'); // Your form fields\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, 'http://www.google.com/');\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($fields));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1); // Return the transfer as a string instead of outputting it directly\ncurl_setopt($ch, CURLOPT_HEADER, 1); // Include the headers in the output\n\n$response = curl_exec($ch);\n\n// Get the headers from the response\n$headers = substr($response, 0, curl_getinfo($ch, CURLINFO_HEADER_SIZE));\n\n// Forward the user to the target URL with the same headers\nheader('Location: http://www.google.com/');\nforeach (explode("\n", $headers) as $header) {\n    header($header);\n}\nexit;