// Assuming you've fetched the record details and changed fields into arrays\n$recordDetails = fetchRecordDetails($recordId);\n$changedFields = fetchChangedFields($recordId);\n\n// While displaying the fields\nforeach ($recordDetails as $field => $value) {\n    if (in_array($field, $changedFields)) {\n        echo "* "; // Indicator for changed field\n    }\n    echo "$field: $value\n";\n}\n\nfunction fetchChangedFields($recordId) {\n    $sql = "SELECT DISTINCT changed_fields FROM audit_trail WHERE record_id = :recordId";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute(['recordId' => $recordId]);\n    $changedFields = [];\n    while ($row = $stmt->fetch()) {\n        $changedFields = array_merge($changedFields, explode(',', $row['changed_fields']));\n    }\n    return array_unique($changedFields); // Return unique fields that have changed\n}