function readLastLine($filename) {\n    $line = '';\n    $fp = fopen($filename, 'r');\n    $cursor = -1;\n    fseek($fp, $cursor, SEEK_END);\n    $char = fgetc($fp);\n    // Trim trailing newline characters in the file\n    while ($char === "\n" || $char === "\r") {\n        fseek($fp, --$cursor, SEEK_END);\n        $char = fgetc($fp);\n    }\n    // Read until a newline or the start of the file\n    while ($char !== false && $char !== "\n" && $char !== "\r") {\n        $line = $char . $line;\n        fseek($fp, --$cursor, SEEK_END);\n        $char = fgetc($fp);\n    }\n    fclose($fp);\n    return $line;\n}\n\n$lastLine = readLastLine('log.txt');\necho $lastLine;