$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_errno) {\n    echo "Failed to connect to MySQL: (" . $mysqli->connect_errno . ") " . $mysqli->connect_error;\n}\n\n$sp_query = "DELIMITER // CREATE PROCEDURE my_procedure() BEGIN SELECT * FROM my_table; END // DELIMITER ;";\n\nif ($mysqli->query($sp_query)) {\n    echo "Stored procedure created successfully";\n} else {\n    echo "Error creating stored procedure: (" . $mysqli->errno . ") " . $mysqli->error;\n}\n\n$mysqli->close();