function isDomainAvailable($domain) {\n    $whoisServers = array(\n        'com' => 'whois.verisign-grs.com',\n        'net' => 'whois.verisign-grs.com',\n        'org' => 'whois.pir.org',\n        // Add more TLDs as needed\n    );\n\n    $tld = substr($domain, -3); // Get the TLD (e.g. .com, .net, etc.)\n    $whoisServer = $whoisServers[$tld];\n\n    if (!$whoisServer) {\n        return false; // Unsupported TLD\n    }\n\n    $fp = fsockopen($whoisServer, 43);\n    if (!$fp) {\n        return false; // Connection failed\n    }\n\n    fwrite($fp, $domain . "\r\n");\n    $response = '';\n    while (!feof($fp)) {\n        $response .= fgets($fp);\n    }\n    fclose($fp);\n\n    // Check if the domain is available\n    if (strpos($response, 'No match for') !== false) {\n        return true; // Domain is available\n    } else {\n        return false; // Domain is not available\n    }\n}\n\n// Example usage:\n$domain = 'example.com';\nif (isDomainAvailable($domain)) {\n    echo "The domain $domain is available.";\n} else {\n    echo "The domain $domain is not available.";\n}