class Benchmark {\n    public function reflection() {\n        $class = new ReflectionClass('Test');\n        $method = $class->getMethod('test');\n        $method->invoke(new Test());\n    }\n\n    public function noReflection() {\n        $test = new Test();\n        $test->test();\n    }\n}\n\nclass Test {\n    public function test() {}\n}\n\n$benchmark = new Benchmark();\n\n// Warm-up\nfor ($i = 0; $i < 1000; $i++) {\n    $benchmark->reflection();\n    $benchmark->noReflection();\n}\n\n// Benchmark\n$start = microtime(true);\nfor ($i = 0; $i < 10000; $i++) {\n    $benchmark->reflection();\n}\n$reflectionTime = microtime(true) - $start;\n\n$start = microtime(true);\nfor ($i = 0; $i < 10000; $i++) {\n    $benchmark->noReflection();\n}\n$noReflectionTime = microtime(true) - $start;\n\necho "Reflection time: $reflectionTime seconds\n";\necho "No reflection time: $noReflectionTime seconds\n";