// logincheck.php ( handles the form submission)\nrequire_once 'config.php';\nrequire_once 'tweetphp/TweetPHP.php';\n\n$tweetphp = new TweetPHP(TWITTER_CONSUMER_KEY, TWITTER_CONSUMER_SECRET);\n\n// Get a request token\n$token = $tweetphp->getRequestToken();\n\n// Redirect the user to Twitter to authorize\nheader('Location: https://api.twitter.com/oauth/authorize?oauth_token=' . $token['oauth_token']);\nexit;