function check($str1, $str2) {\n    $distance = jaroWinklerDistance($str1, $str2);\n    $threshold = 0.7; // adjust this value to suit your needs\n    return $distance >= $threshold;\n}\n\nfunction jaroWinklerDistance($str1, $str2) {\n    $len1 = strlen($str1);\n    $len2 = strlen($str2);\n    $match = 0;\n    $k = 0;\n    $transpositions = 0;\n    for ($i = 0; $i < $len1; $i++) {\n        if ($str1[$i] == $str2[$k]) {\n            $match++;\n            $k++;\n        } else {\n            $transpositions++;\n        }\n    }\n    $distance = ($match / $len1 + $match / $len2 + ($match - $transpositions) / $match) / 3;\n    return $distance;\n}