$stmt = $mysqli->prepare("SELECT * FROM table WHERE NOT column=0");\n$stmt->execute();\n$results = $stmt->get_result();\n\nforeach ($results as $result) {\n    $nox = $result['field_x'];\n    // Use $nox for whatever\n\n    // Update query using prepared statement\n    $updateStmt = $mysqli->prepare("UPDATE table SET field_y = ? WHERE field_x = ?");\n    $updateStmt->bind_param("si", $newValue, $nox);\n    $updateStmt->execute();\n    $updateStmt->close();\n}\n\n$stmt->close();