<?php\n$prefix = get_current_user() . '_';\n\n$host = 'your_host';\n$user = 'your_user';\n$password = 'your_password';\n$dbName = $prefix . 'your_db_name';\n$dbUser = $prefix . 'your_db_user';\n$dbPassword = 'your_db_password';\n\n$mysqli = new mysqli($host, $user, $password);\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "CREATE DATABASE IF NOT EXISTS $dbName";\n$mysqli->query($query);\n\n$query = "CREATE USER '$dbUser'@'$host' IDENTIFIED BY '$dbPassword'";\n$mysqli->query($query);\n\n$query = "GRANT SELECT,INSERT,UPDATE,DELETE ON $dbName.* TO '$dbUser'@'$host'";\n$mysqli->query($query);\n\n$sqlFile = 'path/to/your/sqlfile.sql';\n$mysqli->select_db($dbName);\n$mysqli->multi_query(file_get_contents($sqlFile));\n\n$mysqli->close();\n?>