function appendDepth(array $array, int $depth = 1): array\n{\n    foreach ($array as $key => $value) {\n        if (is_array($value)) {\n            $array[$key] = appendDepth($value, $depth + 1);\n        }\n        $array[$key]['depth'] = $depth;\n    }\n    return $array;\n}\n\n$example = [\n    // your example array here\n];\n\n$exampleWithDepth = appendDepth($example);\n\nprint_r($exampleWithDepth);