$url = 'https://s3.amazonaws.com/your-bucket/unique-id';\n$renameTo = 'new-filename.txt';\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, false);\ncurl_setopt($ch, CURLOPT_WRITEFUNCTION, function($ch, $data) {\n    echo $data;\n    flush();\n    return strlen($data);\n});\n\nheader('Content-Type: application/octet-stream');\nheader('Content-Disposition: attachment; filename="' . $renameTo . '"');\n\ncurl_exec($ch);\ncurl_close($ch);