declare(strict_types=1);\n\nclass TestClass\n{\n    private $declaredVar;\n\n    function __construct()\n    {\n        $this->declaredVar = "I am a declared variable.";\n    }\n\n    function display()\n    {\n        echo $this->declaredVar . "<br />";\n    }\n\n    public function __set($name, $value)\n    {\n        throw new Exception("Cannot create property $name dynamically.");\n    }\n}\n\n$test = new TestClass;\n$test->display();\n\ntry {\n    $test->undeclaredVar = "I wasn't declared, but I still work.";\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}