$log_file = '/path/to/apache/error.log';\n\n// Define a regular expression pattern to match error log entries\n$pattern = '/\[(.*?)\] \[(.*?)\] (?:PHP )?(.*)/';\n\n// Open the log file in read mode\n$handle = fopen($log_file, 'r');\n\n// Read the log file line by line\nwhile (($line = fgets($handle)) !== false) {\n    // Use the regular expression pattern to match the log entry\n    if (preg_match($pattern, $line, $matches)) {\n        // Extract the timestamp, error level, and error message\n        $timestamp = $matches[1];\n        $error_level = $matches[2];\n        $error_message = $matches[3];\n\n        // Print or process the extracted information\n        echo "Timestamp: $timestamp\n";\n        echo "Error Level: $error_level\n";\n        echo "Error Message: $error_message\n";\n    }\n}\n\n// Close the log file handle\nfclose($handle);