// Sample data from database\n$data = array(\n    array('id' => 1, 'name' => 'John'),\n    array('id' => 1, 'name' => 'Jane'),\n    array('id' => 2, 'name' => 'Bob'),\n    array('id' => 2, 'name' => 'Alice'),\n    array('id' => 3, 'name' => 'Charlie')\n);\n\n// Create a new DOMDocument\n$doc = new DOMDocument();\n$doc->formatOutput = true;\n\n// Create a root node\n$root = $doc->createElement('root');\n$doc->appendChild($root);\n\n// Group data by id\n$groups = array();\nforeach ($data as $item) {\n    if (!isset($groups[$item['id']])) {\n        $groups[$item['id']] = array();\n    }\n    $groups[$item['id']][] = $item;\n}\n\n// Create nodes based on groups\nforeach ($groups as $id => $items) {\n    $groupNode = $doc->createElement('group');\n    $groupNode->setAttribute('id', $id);\n    $root->appendChild($groupNode);\n\n    foreach ($items as $item) {\n        $itemNode = $doc->createElement('item');\n        $itemNode->setAttribute('name', $item['name']);\n        $groupNode->appendChild($itemNode);\n    }\n}\n\n// Output the XML\necho $doc->saveXML();