$doc = new DOMDocument();\n$doc->load('http://ws.audioscrobbler.com/2.0/artist/beatles/images.rss');\n$arrFeeds = array();\nforeach ($doc->getElementsByTagName('item') as $node) {\n    $itemRSS = array (\n        'title' => $node->getElementsByTagName('title')->item(0)->nodeValue,\n        'desc' => $node->getElementsByTagName('description')->item(0)->nodeValue,\n        'link' => $node->getElementsByTagName('link')->item(0)->nodeValue,\n        'date' => $node->getElementsByTagName('pubDate')->item(0)->nodeValue\n    );\n    \n    // Get media:content and media:thumbnail elements\n    $mediaContent = $node->getElementsByTagNameNS('http://search.yahoo.com/mrss/', 'content')->item(0);\n    $mediaThumbnail = $node->getElementsByTagNameNS('http://search.yahoo.com/mrss/', 'thumbnail')->item(0);\n    \n    if ($mediaContent) {\n        $itemRSS['mediaContentUrl'] = $mediaContent->getAttribute('url');\n    }\n    \n    if ($mediaThumbnail) {\n        $itemRSS['mediaThumbnailUrl'] = $mediaThumbnail->getAttribute('url');\n    }\n    \n    array_push($arrFeeds, $itemRSS);\n}