$dsn = 'mysql:host=primary_host;dbname=my_database';\n$username = 'my_username';\n$password = 'my_password';\n\n// Create a PDO object with the primary host\n$pdo = new PDO($dsn, $username, $password);\n\n// Define a failover host\n$failover_host = 'secondary_host';\n\ntry {\n    // Try to connect to the primary host\n    $pdo->exec('SELECT 1');\n} catch (PDOException $e) {\n    // If the primary host is down, connect to the failover host\n    $dsn = 'mysql:host=' . $failover_host . ';dbname=my_database';\n    $pdo = new PDO($dsn, $username, $password);\n}