// Example of a secure PHP class\nclass User {\n    private $name;\n    private $email;\n\n    public function __construct($name, $email) {\n        $this->name = htmlspecialchars($name);\n        $this->email = filter_var($email, FILTER_VALIDATE_EMAIL);\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n\n    public function getEmail() {\n        return $this->email;\n    }\n}