function cleanFile($file1, $file2) {\n  $input_file = fopen($file1, "r");\n  $output_file = fopen($file2, "w");\n  $chunk_size = 4096; // adjust this value to suit your needs\n\n  while (!feof($input_file)) {\n    $chunk = fread($input_file, $chunk_size);\n    if ($chunk === false) {\n      break;\n    }\n\n    // process the chunk\n    $chunk = str_replace('<text>', '<text><![CDATA[', $chunk);\n    $chunk = str_replace('</text>', ']]></text>', $chunk);\n\n    // write the processed chunk to the output file\n    fwrite($output_file, $chunk);\n  }\n\n  fclose($input_file);\n  fclose($output_file);\n}