$mysqli = new mysqli('host', 'username', 'password', 'database');\n\n// Start transaction\n$mysqli->begin_transaction(MYSQLI_TRANS_START_READ_WRITE);\n\n// Lock table or row(s)\n$mysqli->query("LOCK TABLES mytable WRITE");\n// or\n$mysqli->query("SELECT * FROM mytable WHERE column1 = 'value1' FOR UPDATE");\n\n// Perform insert operation\n$mysqli->query("INSERT INTO mytable (column1, column2) VALUES ('value1', 'value2')");\n\n// Commit transaction\n$mysqli->commit();\n\n// Release lock (if using LOCK TABLES)\n$mysqli->query("UNLOCK TABLES");