<?php\n$proxyServer = 'https://your-proxy-server.com:8080';\n$targetUrl = 'https://example.com/healthcheck';\n$curlCmd = "curl -x $proxyServer -I $targetUrl";\n\n$output = shell_exec($curlCmd);\n$httpCode = substr($output, 9, 3); // extract HTTP status code\n\nif ($httpCode != '200') {\n    // send alert notification (e.g., email, SMS, or webhook)\n    echo "Proxy server is not responding correctly!";\n}\n?>