// Generate a salt\n$salt = bin2hex(random_bytes(16));\n\n// Generate a pepper (optional)\n$pepper = bin2hex(random_bytes(16));\n\n// Generate a key (unique to each user)\n$key = bin2hex(random_bytes(32));\n\n// Define the work factor (e.g., iteration count)\n$workFactor = 10000;\n\n// Hash the password\n$hashedPassword = hash_hmac('sha512', str_repeat($salt . $password . $pepper, $workFactor), $key);\n\n// Store the hashed password, salt, and key in the database