$result = mysqli_query($conn, $sql);\n\n// Initialize arrays to store the counts\n$weekday_counts = array();\n$weekend_counts = array();\n$monthly_counts = array();\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n  $username = $row['username'];\n  $day_type = $row['day_type'];\n  $event_date = $row['eventDate'];\n  $month = date('F', strtotime($event_date)); // Get the month name\n\n  // Increment the weekday/weekend count for the user\n  if ($day_type == 'weekday') {\n    $weekday_counts[$username]++;\n  } else {\n    $weekend_counts[$username]++;\n  }\n\n  // Increment the monthly count for the user\n  $monthly_counts[$username][$month]++;\n}\n\n// Print the results in an HTML table\necho '<table>';\necho '<tr><th>Username</th><th>Weekday Count</th><th>Weekend Count</th><th>Monthly Counts</th></tr>';\nforeach ($weekday_counts as $username => $count) {\n  echo '<tr>';\n  echo '<td>' . $username . '</td>';\n  echo '<td>' . $count . '</td>';\n  echo '<td>' . $weekend_counts[$username] . '</td>';\n  echo '<td>';\n  foreach ($monthly_counts[$username] as $month => $count) {\n    echo $month . ': ' . $count . '<br>';\n  }\n  echo '</td>';\n  echo '</tr>';\n}\necho '</table>';