$countries = array(\n    "217" => "Turkenistan",\n    "218" => "Turks and Caicos Islands",\n    "219" => "Tuvalu",\n    "220" => "Uganda",\n    "221" => "Ukraine",\n    "222" => "United Arab Emirates",\n    "223" => "United Kingdom (Great Britain)",\n    "224" => "United States"\n);\n\n$mycountry = 224;\n\n// Move USA and UK to the top of the list\n$usa = array_splice($countries, 224, 1);\n$uk = array_splice($countries, 223, 1);\n$countries = array_merge($usa + $uk, $countries);\n\n// Create the dropdown select form\necho '<select name="country" style="width:180px;" onChange="do_get_rest_popup(this.value)">';\n\nforeach ($countries as $id => $country) {\n    $selected = ($id == $mycountry) ? ' SELECTED' : '';\n    echo "<option value=\"$id\"$selected>$country</option>";\n}\n\necho '</select>';