$resource = NewMagickWand();\nMagickReadImage($resource, $image);\nMagickSetImageCompressionQuality($resource, 100);\n$resource = MagickTransformImage($resource, '0x0', '660x500');\n\n// Write the resized image back to a file\nMagickWriteImage($resource, $image); // You might want to change $image to a different path to save the resized image separately\n\n// Don't forget to destroy the MagickWand resource to free up system resources\nDestroyMagickWand($resource);