use PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\SMTP;\nuse PHPMailer\PHPMailer\Exception;\n\nfunction emailUser($table, $subject, $message) {\n    $query = "SELECT * FROM $table";\n    $result = mysql_query($query);\n\n    $mail = new PHPMailer(true);\n\n    $mail->SMTPDebug = SMTP::DEBUG_SERVER;\n    $mail->isSMTP();\n    $mail->Host = 'mail.site.net';\n    $mail->Port = 25;\n    $mail->SMTPAuth = true;\n    $mail->Username = 'email@site.net';\n    $mail->Password = 'your_password'; // Make sure to use a secure password\n\n    $mail->setFrom('email@site.net');\n    $mail->addReplyTo('email@site.net');\n\n    while ($row = mysql_fetch_array($result)) {\n        $mail->addAddress($row['email']);\n    }\n\n    $mail->isHTML(true);\n    $mail->Subject = $subject;\n    $mail->Body = $message;\n\n    if (!$mail->send()) {\n        echo 'Mailer Error: ' . $mail->ErrorInfo;\n    } else {\n        echo 'Message sent successfully!';\n    }\n}