$input = "Ryu Aiter D78:21:87:13 177 /177 1 / 6\nRyu Chronos D78:21:26:21 182 /182 0 / 6\n...";\n\n$rows = explode("\n", $input);\n\nforeach ($rows as $row) {\n    preg_match('/^([a-zA-Z0-9\s]{1,16})\s(D[0-9:]{12})\s(\d+)\s\/\s(\d+)\s(\d+)\s\/\s(\d+)/', $row, $matches);\n    \n    $base = trim($matches[1]);\n    $location = trim($matches[2]);\n    $econ = (int)$matches[3];\n    $maxEcon = (int)$matches[4];\n    $used = (int)$matches[5];\n    $total = (int)$matches[6];\n    \n    // Do something with the extracted data\n    echo "Base: $base, Location: $location, Econ: $econ/$maxEcon, Used/Total: $used/$total\n";\n}