<?php\n// Assuming you have a function to retrieve music details from your database\n$musicDetails = getMusicDetailsFromDatabase($musicId);\n\n// Function to generate XSPF content on the fly\nfunction generateXSPFContent($musicDetails) {\n    header('Content-Type: application/xspf+xml');\n    echo '<?xml version="1.0" encoding="UTF-8"?>';\n    echo '<playlist version="1" xmlns="http://xspf.org/ns/0/">';\n    echo '<trackList>';\n    foreach ($musicDetails as $track) {\n        echo '<track>';\n        echo '<title>' . htmlspecialchars($track['title'], ENT_QUOTES, 'UTF-8') . '</title>';\n        echo '<location>' . htmlspecialchars($track['file_location'], ENT_QUOTES, 'UTF-8') . '</location>';\n        // Add any other necessary metadata here\n        echo '</track>';\n    }\n    echo '</trackList>';\n    echo '</playlist>';\n}\n\n// Usage\ngenerateXSPFContent($musicDetails);\n?>