function cartesianProduct($arrays) {\n    $result = array();\n    $arrays = array_values($arrays);\n    $sizeIn = sizeof($arrays);\n    $size = $sizeIn > 0 ? 1 : 0;\n    foreach ($arrays as $array)\n        $size = $size * sizeof($array);\n    for ($i = 0; $i < $size; $i ++)\n    {\n        $result[$i] = array();\n        for ($j = 0; $j < $sizeIn; $j ++)\n            array_push($result[$i], current($arrays[$j]));\n        for ($j = ($sizeIn - 1); $j >= 0; $j --)\n        {\n            if (next($arrays[$j]))\n                break;\n            elseif (isset ($arrays[$j]))\n                reset($arrays[$j]);\n        }\n    }\n    return $result;\n}\n\n$sizes =   array(\n    'Shirt Size' => array('Small', 'Medium', 'Large'),\n    'Sleeve Size' => array('Short', 'Long')\n); \n\n$colors =  array(\n    'Shirt Color' => array('Black', 'Red'),\n    'Sleeve Color' => array('White', 'Orange')\n);\n\n$sizeCombinations = cartesianProduct($sizes);\n$colorCombinations = cartesianProduct($colors);\n\n$result = array();\nforeach ($sizeCombinations as $sizeCombination) {\n    foreach ($colorCombinations as $colorCombination) {\n        $row = array_merge($sizeCombination, $colorCombination);\n        $result[] = $row;\n    }\n}\n\n// Print the result\nprint_r($result);