use PHPUnit\Framework\TestCase;\nuse Mockery as m;\n\nclass MyClassTest extends TestCase\n{\n    public function testMyMethod()\n    {\n        $otherClassMock = m::mock(OtherClassInterface::class);\n        $otherClassMock->shouldReceive('someMethod')->andReturn('mocked result');\n\n        $myClass = new MyClass($otherClassMock);\n        $result = $myClass->myMethod();\n\n        # assertions\n    }\n}