// Instead of executing separate queries for article content and author info...\n$query1 = "SELECT * FROM articles WHERE id = ?";\n$query2 = "SELECT * FROM users WHERE id = ?";\n\n// Combine the queries using a join\n$query = "\n    SELECT articles.*, users.*\n    FROM articles\n    LEFT JOIN users ON articles.author_id = users.id\n    WHERE articles.id = ?\n";\n\n// Execute the combined query\n$stmt = $mysqli->prepare($query);\n$stmt->bind_param("i", $article_id);\n$stmt->execute();