$date1 = '11/11/2002'; // d/m/Y format\n$date2 = '11-11-2002'; // d-m-Y format\n\n$dateFormat = 'm/d/Y'; // desired output format\n\n$date1_converted = DateTime::createFromFormat('d/m/Y', $date1)->format($dateFormat);\n$date2_converted = DateTime::createFromFormat('d-m-Y', $date2)->format($dateFormat);\n\necho $date1_converted . "\n"; // outputs: 11/11/2002\necho $date2_converted . "\n"; // outputs: 11/11/2002