$allowed_columns = array('field1', 'field2', 'field3', ...); // define your allowed columns\n\n$columns = implode(', ', $allowed_columns); // create a comma-separated string\n\n$query = "SELECT $columns FROM tablename WHERE field1 = ? && field2 = ?"; // use the whitelist\n\n$stmt = $mysqli->prepare($query);\n$stmt->bind_param('ss', $value1, $value2); // bind your values\n$stmt->execute();