// Assuming you have an image file named 'image.jpg' and want to split it into 250 tiles\n$image = 'image.jpg';\n$tileSize = 20; // adjust this value to change the tile size\n$tileCount = 250;\n\n// Create tiles\nfor ($i = 0; $i < $tileCount; $i++) {\n    $x = ($i % $tileCount) * $tileSize;\n    $y = floor($i / $tileCount) * $tileSize;\n    $tileImage = imagecrop($image, $x, $y, $tileSize, $tileSize);\n    imagejpeg($tileImage, "tile_{$i}.jpg");\n}