// Prepare a SQL statement to select the text from the database\n$stmt = $pdo->prepare('SELECT text_field FROM your_table WHERE id = :id');\n\n// Bind the ID to the prepared statement\n$stmt->bindParam(':id', $id);\n\n// Execute the prepared statement\n$stmt->execute();\n\n// Fetch the result\n$result = $stmt->fetch();\n\n// Display the text exactly as it was entered by the user\necho $result['text_field'];