$table = new MyTable(); // your Zend_Db_Table object\n\n// Set default value of NULL for the 'my_integer_field' and 'my_date_field' columns\n$table->setDefaultValue('my_integer_field', null);\n$table->setDefaultValue('my_date_field', null);\n\n// Now, when you insert data, blank values will be inserted as NULL\n$data = array(\n    'my_integer_field' => '', // will be inserted as NULL\n    'my_date_field' => '', // will be inserted as NULL\n);\n$table->insert($data);