$xml = '<root>\n  <thing>\n    <specs>\n      <spec1>Value 1</spec1>\n      <spec3>Value 3</spec3>\n      <spec2>Value 2</spec2>\n    </specs>\n    <details />\n    <more_info>\n      <info1 />\n      <info2 />\n    </more_info>\n  </thing>\n</root>';\n\n$reader = new XMLReader();\n$reader->XML($xml);\n\nwhile ($reader->read()) {\n    if ($reader->nodeType === XMLReader::ELEMENT && $reader->name === 'specs') {\n        while ($reader->read()) {\n            if ($reader->nodeType === XMLReader::ELEMENT && in_array($reader->name, ['spec1', 'spec2', 'spec3'])) {\n                echo $reader->name . ': ' . $reader->readString() . "\n";\n            }\n            if ($reader->nodeType === XMLReader::END_ELEMENT && $reader->name === 'specs') {\n                break;\n            }\n        }\n    }\n}