function convert_to_xml_friendly($string) {\n    $string = mb_convert_encoding($string, 'UTF-8', 'UTF-8');\n    $string = htmlentities($string, ENT_QUOTES, 'UTF-8');\n    return $string;\n}\n\n// Example usage:\n$string1 = "Côte d'Ivoire";\n$string2 = "ÖFB Stiegl Cup";\n\n$xml_friendly_string1 = convert_to_xml_friendly($string1);\n$xml_friendly_string2 = convert_to_xml_friendly($string2);\n\necho $xml_friendly_string1 . "\n";  // Output: C&#244;te d'Ivoire\necho $xml_friendly_string2 . "\n";  // Output: &#214;FB Stiegl Cup