function FilterALLHTML($var) {\n    if ($var !== null) {\n        $text = strip_tags($var);\n        $search = [\n            "/f.?u.?c.?k/i",\n            "/(s|$).?h.?i.?t/i",\n            '/(potspace|mycrib|palbolt|pot space)/i'\n        ];\n        return preg_replace($search, '', $text);\n    }\n    return null;\n}\n\n$variables = [$city, $state, $title, $division];\n$filteredVariables = array_map('FilterALLHTML', $variables);\n\nlist($city, $state, $title, $division) = $filteredVariables;