function parse_xml($raw_xml, $xpath) {\n    $xml = simplexml_load_string($raw_xml);\n    $data = array();\n    foreach ($xml->xpath($xpath) as $node) {\n        // Process the node data\n        $data[] = (array)$node;\n    }\n    return $data;\n}\n\n$raw_xml = file_get_contents('feed1.xml');\n$data1 = parse_xml($raw_xml, '/channel');\n\n$raw_xml2 = file_get_contents('feed2.xml');\n$data2 = parse_xml($raw_xml2, '/item');