function getDirectorySize($directory) {\n    $size = 0;\n    $files = scandir($directory);\n    foreach ($files as $file) {\n        if ($file != '.' && $file != '..') {\n            $filePath = $directory . '/' . $file;\n            if (is_dir($filePath)) {\n                $size += getDirectorySize($filePath);\n            } elseif (is_file($filePath)) {\n                $size += filesize($filePath);\n            }\n        }\n    }\n    return $size;\n}\n\n$rootDirectory = '/path/to/your/main/folder';\n$dirSizes = array();\n\n$directories = scandir($rootDirectory);\nforeach ($directories as $directory) {\n    if ($directory != '.' && $directory != '..') {\n        $dirPath = $rootDirectory . '/' . $directory;\n        if (is_dir($dirPath)) {\n            $size = getDirectorySize($dirPath);\n            $dirSizes[$directory] = $size;\n        }\n    }\n}\n\n// Print the directory sizes\nforeach ($dirSizes as $directory => $size) {\n    echo "$directory: " . formatSize($size) . "\n";\n}\n\n// Function to format the size\nfunction formatSize($size) {\n    $units = array('B', 'KB', 'MB', 'GB', 'TB');\n    $index = 0;\n    while ($size >= 1024 && $index < count($units) - 1) {\n        $size /= 1024;\n        $index++;\n    }\n    return round($size, 2) . ' ' . $units[$index];\n}