$separator = 'GIF_SEPARATOR';\n\n// Read uploaded file\n$uploadedFile = fopen($_FILES['file']['tmp_name'], 'rb');\n$binaryData = fread($uploadedFile, filesize($_FILES['file']['tmp_name']));\nfclose($uploadedFile);\n\n// Split binary data into images\n$images = explode($separator, $binaryData);\n\n// Loop through images\nforeach ($images as $image) {\n    // Skip empty images\n    if (empty($image)) continue;\n    \n    // Create new image file\n    $newFile = fopen('image_' . uniqid() . '.gif', 'wb');\n    fwrite($newFile, $image);\n    fclose($newFile);\n}