$search_query = mysqli_real_escape_string($db_link, $_GET['q']);\n$snippet_length = 150;\n\n$query = "SELECT * FROM table WHERE MATCH (column) AGAINST ('$search_query' IN BOOLEAN MODE)";\n$result = mysqli_query($db_link, $query);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $text = strip_tags($row['column']);\n    $snippet = substr($text, 0, $snippet_length);\n    $highlighted_snippet = highlight_string($snippet, $search_query);\n\n    echo "<p>$highlighted_snippet</p>";\n}\n\nfunction highlight_string($text, $search_query) {\n    $words = explode(' ', $search_query);\n    foreach ($words as $word) {\n        $text = preg_replace("/\b$word\b/i", "<mark>$word</mark>", $text);\n    }\n    return $text;\n}