// Generate a unique alias\n$alias = hash('sha256', uniqid(mt_rand(), true)) . '@yourdomain.com';\n\n// Store the alias and real email in a database\n$db = new PDO('mysql:host=yourhost;dbname=yourdb', 'youruser', 'yourpass');\n$stmt = $db->prepare('INSERT INTO email_aliases (alias, real_email) VALUES (:alias, :real_email)');\n$stmt->execute([':alias' => $alias, ':real_email' => 'user@example.com']);\n\n// Setup email forwarding (this will depend on your email server configuration)\n// For example, using Postfix, you can add a virtual alias map:\n// alias_maps = hash:/etc/postfix/virtual\n// virtual_alias_maps = hash:/etc/postfix/virtual