trait MessageTrait {\n    protected static $message = "UNTOUCHED";\n\n    public static function yeah() {\n        self::$message = "YEAH";\n    }\n\n    public static function nope() {\n        self::$message = "NOPE";\n    }\n\n    public static function debug() {\n        return self::$message;\n    }\n}\n\nclass Parent {\n    use MessageTrait;\n}\n\nclass Child extends Parent {}\n\nParent::yeah();\necho Parent::debug();  // "YEAH"\n\nChild::nope();\necho Child::debug();  // "NOPE"