abstract class Parent {\n    protected $message = "UNTOUCHED";\n\n    public function yeah() {\n        $this->message = "YEAH";\n    }\n\n    public function nope() {\n        $this->message = "NOPE";\n    }\n\n    public function debug() {\n        return $this->message;\n    }\n}\n\nclass Child extends Parent {}\n\n$obj = new Child();\n$obj->yeah();\necho $obj->debug();  // "YEAH"\n\n$obj->nope();\necho $obj->debug();  // "NOPE"