function save_pic($pic_url, $pk) {\n    $imageDir = './';\n    if (!strlen($pic_url))\n        return "removed.jpg";\n    if (!is_dir($imageDir) || !is_writable($imageDir)) {\n        return "removed.jpg";\n    }\n    try {\n        $image = file_get_contents($pic_url);\n        if (empty($image)) {\n            return "removed.jpg";\n        }\n        $r = file_put_contents($imageDir.$pk.".jpg", $image);\n        if ($r) {\n            return "./$pk.jpg"; \n        } else {\n            return "removed.jpg";\n        }\n    } catch (Exception $e) {\n        return "removed.jpg";\n    }\n}