function clean($text) {\n    $msg = mailparse_msg_parse($text);\n    $parts = mailparse_msg_get_structure($msg);\n    $html = '';\n\n    foreach ($parts as $part) {\n        $part_data = mailparse_msg_get_part_data($msg, $part);\n        if ($part_data['content-type'] == 'text/plain') {\n            $html .= '<p>' . mailparse_msg_extract_part($msg, $part, NULL) . '</p>';\n        } elseif ($part_data['content-type'] == 'text/html') {\n            $html .= mailparse_msg_extract_part($msg, $part, NULL);\n        }\n    }\n\n    mailparse_msg_free($msg);\n\n    return $html;\n}\n\n$text = '...your MIME data...';\necho clean($text);