// class definition\nclass User {\n    public $fname;\n    public $lname;\n}\n\n// array of objects of the class defined above\n$objUser1 = new User();\n$objUser2 = new User();\n$objUser3 = new User();\n$objUser4 = new User();\n\n$alUser = array();\n$alUser[] = $objUser1;\n$alUser[] = $objUser2;\n$alUser[] = $objUser3;\n$alUser[] = $objUser4;\n\n// iterate and extract values\nforeach($alUser as $user) {\n    echo $user->fname . " " . $user->lname . "\n";\n}